query:
-- 先删除产品图片
DELETE FROM productImages WHERE productId IN (
  SELECT id FROM products WHERE categoryId = 1 ORDER BY createdAt DESC LIMIT 2
);

-- 再删除产品
DELETE FROM products WHERE id IN (
  SELECT id FROM (
    SELECT id FROM products WHERE categoryId = 1 ORDER BY createdAt DESC LIMIT 2
  ) AS temp
);

